mapper.update:
   	UPDATE product
   	
   		prod_name 	= #{prodName} ,
		prod_detail	= #{prodDetail},
		MANUFACTURE_DAY		= #{manuDate}
   	
   	WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT prod_name , price
					FROM product
					
						
							
					 			prod_name = #{searchKeyword}
							
							
					 			price = #{searchKeyword}
							
						
					 ) countTable
